import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { useQuery } from "react-query";

async function fetchTeacher(userId) {
  const par = { id: userId };
  const { data } = await axios.post(
    "http://127.0.0.1:8000/api/show-teacher",
    par
  );
  console.log(data.data);
  return data;
}

export default function EditTeacher() {
  let navigate = useNavigate();
  let { userId } = useParams();

  const color = "light";
  const { data, error, isError } = useQuery(
    ["student", userId],
    () => fetchTeacher(userId),
    { retry: false }
  );
  const [name, setName] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");


  const handleSubmit = (e, userId) => {
    e.preventDefault()
    const formData = {
      "id": userId,
      "name" : name === "" ? data?.student.name : name,
      "lastname" : lastname === "" ? data?.student.lastname : lastname,
      "email" : email === "" ? data?.student.email : email,
    }
    console.log(formData)
    axios.post(`http://127.0.0.1:8000/api/update-teacher`, { formData })
   .then(res => {
      console.log(res);
    console.log(res.data);
    })
  }
  if (isError) {
    return <div>Error! {error.message}</div>;
  }
  return (
    <div className="flex flex-wrap mt-4">
      <div className="w-full mb-12 px-4">
        <div
          className={
            "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
            (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
          }
        >
          <div className="rounded-t mb-0 px-4 py-3 border-0">
            <div className="flex flex-wrap items-center">
              <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                <h3
                  className={
                    "font-semibold text-lg " +
                    (color === "light" ? "text-blueGray-700" : "text-white")
                  }
                >
                  Students
                </h3>
              </div>
            </div>
          </div>
          <div className="block w-full pl-24 ">
            <form
              action=""
              class="p-8 mt-6 mb-0 space-y-4 rounded-lg shadow-2xl flex flex-col items-center"
            >
              <div>
                <label for="email" class="text-sm font-medium">
                  name
                </label>

                <div class="relative mt-1">
                  <input
                    type="text"
                    id="email"
                    name="name"
                    class="w-full p-4 pr-12 text-sm border-gray-200 rounded-lg shadow-sm"
                    defaultValue={data?.student.name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </div>
              </div>
              <div>
                <label for="email" class="text-sm font-medium">
                  lastName
                </label>

                <div class="relative mt-1">
                  <input
                    type="text"
                    id="email"
                    name="lastname"
                    class="w-full p-4 pr-12 text-sm border-gray-200 rounded-lg shadow-sm"
                    placeholder="Enter email"
                    defaultValue={data?.student.lastname}
                    onChange={(e) => setLastname(e.target.value)}
                  />
                </div>
              </div>


              <div>
                <label for="email" class="text-sm font-medium">
                  Email
                </label>

                <div class="relative mt-1">
                  <input
                    type="email"
                    id="email"
                    name="email"
                    class="w-full p-4 pr-12 text-sm border-gray-200 rounded-lg shadow-sm"
                    placeholder="Enter email"
                    defaultValue={data?.student.email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>
              </div>

              <button onClick={(e) => handleSubmit(e, data?.student.id)} className=" bg-emerald-400 text-white mr-2 m-2   p-2 rounded  h-14 hover:cursor-pointer">
                Edit
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
